<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class AdministrarTratamientos_model extends CI_Model {	function __construct() {		parent:: __construct();		$this->load->database();	}	function anadirTratamiento($data) {		$this->db->insert(					'TBL_tratamientos', 					array('id'=>$data['id'], 						'nombre'=>$data['nombre'], 						'sistema'=>$data['sistema'],						'descripcion'=>$data['descripcion']));	}	function actualizarTratamiento($data) {		$this->db->where('id', $data['id']);		$this->db->update(		                    'TBL_tratamientos',		                    array('id'=>$data['id'], 						'nombre'=>$data['nombre'], 						'sistema'=>$data['sistema'],						'descripcion'=>$data['descripcion']));	}	function eliminarTratamiento($data) {		$this->db->delete(		                    'TBL_tratamientos',		                    array('id'=>$data['id']));	}	function obtenerTratamiento() {		$query = $this->db->get('TBL_tratamientos');		if($query->num_rows() > 0) {			return $query;		} else {			return false;		}	}	function buscarTratamiento($data) {		$this->db->where('id', $data['id']);		$query = $this->db->get('TBL_tratamientos');		if($query->num_rows() > 0) {			return $query;		} else {			return false;		}	}}?>